import React, { useEffect, useRef, useState } from "react";
import Navbar from "./Navbar";

export default function CravingQuickPick() {
    const [input, setInput] = useState("");
    const [messages, setMessages] = useState([
        { role: "bot", text: "ÏïàÎÖïÌïòÏÑ∏Ïöî! Ïò§Îäò Ïñ¥Îñ§ ÏÉÅÌÉúÏù∏ÏßÄ Í∞ÑÎã®Ìûà ÎßêÌï¥Ï£ºÏãúÎ©¥ ÎßûÏ∂§ Ï°∞Ìï©ÏùÑ Ï†úÏïàÌï¥ÎìúÎ¶¥Í≤åÏöî üòä" },
        { role: "bot", text: "Ïòà) ÌîºÍ≥§Ìï¥Ïöî / Î∞∞Í∞Ä ÎçîÎ∂ÄÎ£©Ìï¥Ïöî / Îî∞ÎúªÌïú Í≤å ÎÅåÎ†§Ïöî / Îã¨Îã¨Ìïú Í≤å Î®πÍ≥† Ïã∂Ïñ¥Ïöî" },
    ]);
    const listRef = useRef(null);

    // Ïä§ÌÅ¨Î°§ ÌïòÎã® Í≥†Ï†ï
    useEffect(() => {
        if (listRef.current) {
            listRef.current.scrollTop = listRef.current.scrollHeight;
        }
    }, [messages]);

    // ÏïÑÏ£º Í∞ÑÎã®Ìïú Í∑úÏπô Í∏∞Î∞ò Î¥á ÏùëÎãµ
    const reply = (userText) => {
        const t = userText.toLowerCase();
        const recs = [];

        if (t.includes("ÌîºÍ≥§") || t.includes("ÌîºÎ°ú")) {
            recs.push("ÏÉùÍ∞ïÍøÄÏ∞® + Ï†ÄÎãπ ÏöîÍ±∞Ìä∏ + Î∞îÎÇòÎÇò");
        }
        if (t.includes("Îî∞Îúª")) {
            recs.push("ÏàúÎëêÎ∂Ä Îã¨Í±ÄÌÉï + Í≥ÑÎûÄÏ∞ú + ÍπÄ");
        }
        if (t.includes("Îã¨Îã¨") || t.includes("Îã®")) {
            recs.push("ÏïΩÏΩ© ÏöîÍ±∞Ìä∏ + Îî∏Í∏∞ / Îã§ÌÅ¨Ï¥àÏΩî Ìïú Ï°∞Í∞Å");
        }
        if (t.includes("ÎçîÎ∂ÄÎ£©") || t.includes("ÏÜåÌôî")) {
            recs.push("Î∂ÄÎìúÎü¨Ïö¥ ÌòÑÎØ∏Ï£Ω + Îã≠Í∞ÄÏä¥ÏÇ¥ Î≥¥Ïö∏ (Ï†ÄÏûêÍ∑π)");
        }

        if (recs.length === 0) {
            return "Ï°∞Í∏à Îçî ÏïåÎ†§Ï§ÑÎûòÏöî? Ïòà) 'ÌîºÍ≥§ÌïòÍ≥† Îî∞ÎúªÌïú Í≤å ÎÅåÎ†§Ïöî' Ï≤òÎüºÏöî.";
        }
        return `Ïò§ÎäòÏùÄ Ïù¥Îü∞ Ï°∞Ìï©ÏùÑ Ï∂îÏ≤úÌï¥Ïöî:\n‚Ä¢ ${recs.join("\n‚Ä¢ ")}`;
    };

    const send = () => {
        const text = input.trim();
        if (!text) return;
        setMessages((m) => [...m, { role: "user", text }]);
        setInput("");

        setTimeout(() => {
            const botText = reply(text);
            setMessages((m) => [...m, { role: "bot", text: botText }]);
        }, 250);
    };

    const onKeyDown = (e) => {
        if (e.key === "Enter" && !e.shiftKey) {
            e.preventDefault();
            send();
        }
    };

    return (
        <div style={s.wrap}>
            {/* ÏÉÅÎã® Ìó§Îçî(ÏÑ†ÌÉù) */}
            <div style={s.header}>
                <h3 style={s.headerTitle}>üí¨ Ï±óÎ¥á ÏºÄÏñ¥</h3>
                <p style={s.headerSub}>ÏßÄÍ∏à ÎäêÎÇåÏùÑ Ìïú Ï§ÑÎ°ú ÎßêÌï¥Ï£ºÎ©¥ Îçî Ï†ïÌôïÌûà ÎèÑÏôÄÏ§ÑÍ≤åÏöî.</p>
            </div>

            {/* Î©îÏãúÏßÄ ÏòÅÏó≠ */}
            <div ref={listRef} style={s.messages}>
                {messages.map((m, i) => (
                    <div
                        key={i}
                        style={{
                            ...s.bubble,
                            ...(m.role === "user" ? s.user : s.bot),
                        }}
                    >
                        {m.text.split("\n").map((line, idx) => (
                            <p key={idx} style={{ margin: 0, whiteSpace: "pre-wrap" }}>
                                {line}
                            </p>
                        ))}
                    </div>
                ))}
                <div style={{ height: 8 }} />
            </div>

            {/* ÏûÖÎ†•Ï∞Ω: ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò Î∞îÎ°ú ÏúÑÏóê Í≥†Ï†ï */}
            <div style={s.inputBar}>
                <textarea
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={onKeyDown}
                    placeholder="Ïòà) ÌîºÍ≥§ÌïòÍ≥† Îî∞ÎúªÌïú Í≤å ÎÅåÎ†§Ïöî"
                    style={s.input}
                    rows={1}
                />
                <button onClick={send} style={s.send}>Î≥¥ÎÇ¥Í∏∞</button>
            </div>

            {/* ÌïòÎã® ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò */}
            <Navbar />
        </div>
    );
}

const NAV_HEIGHT = 56; // Navbar ÎÜíÏù¥ Ï∂îÏ†ï
const INPUT_HEIGHT = 54;

const s = {
    wrap: {
        position: "relative",
        height: "100vh",
        background: "#fff",
        color: "#2f2f2f",
        display: "flex",
        flexDirection: "column",
        // ÏûÖÎ†•Î∞îÏôÄ ÎÑ§ÎπÑ ÏòÅÏó≠ÎßåÌÅº ÏïÑÎûò Ìå®Îî©
        paddingBottom: NAV_HEIGHT + INPUT_HEIGHT + 12,
        boxSizing: "border-box",
    },
    header: {
        padding: "14px 16px 8px",
        borderBottom: "1px solid #eee",
    },
    headerTitle: { margin: 0, color: "#333" },
    headerSub: { margin: "6px 0 0", color: "#666", fontSize: 13 },
    messages: {
        flex: 1,
        overflowY: "auto",
        padding: "12px 12px 8px",
    },
    bubble: {
        maxWidth: "78%",
        padding: "10px 12px",
        borderRadius: 12,
        margin: "6px 0",
        lineHeight: 1.45,
        fontSize: 15,
        boxShadow: "0 1px 2px rgba(0,0,0,0.04)",
    },
    bot: {
        background: "#f1f3f5",
        color: "#333",
        marginRight: "auto",
        borderTopLeftRadius: 4,
    },
    user: {
        background: "#e66ca8",
        color: "#fff",
        marginLeft: "auto",
        borderTopRightRadius: 4,
    },
    inputBar: {
        position: "fixed",
        left: 0,
        right: 0,
        bottom: NAV_HEIGHT, // ÎÑ§ÎπÑ ÏúÑÏóê Î∂ôÏù¥Í∏∞
        display: "flex",
        gap: 8,
        alignItems: "center",
        padding: "8px 10px",
        background: "#fff",
        borderTop: "1px solid #eee",
        zIndex: 101,
    },
    input: {
        flex: 1,
        resize: "none",
        padding: "10px 12px",
        borderRadius: 10,
        border: "1px solid #e3e3e3",
        outline: "none",
        fontSize: 14,
    },
    send: {
        background: "#e66ca8",
        color: "#fff",
        border: "none",
        borderRadius: 10,
        padding: "10px 14px",
        fontSize: 14,
        cursor: "pointer",
    },
};
